package DatatypesAndVariables;

public class ArithmaticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=50;
         int b=60;
         
         //Addition
         System.out.println(a+b);
         //Sustration
System.out.println(b-a);

//Multiplication
System.out.println(a*b);
//Division
System.out.println(a/b);
//Remainder
System.out.println(a%b);
	}

}
